function getIndicesOf(searchStr, str) {
    var searchStrLen = searchStr.length;
    if (searchStrLen == 0) {
        return [];
    }
    var startIndex = 0, index, indices = [];
    
    while ((index = str.toLowerCase().indexOf(searchStr.toLowerCase(), startIndex)) > -1) {
        indices.push(index);
        startIndex = index + searchStrLen;
    }
    for (i=indices.length-1; i >= 0; i--) {
        titleSlice = str.slice(indices[i],indices[i]+searchStr.length)
        str = str.slice(0,(indices[i])) + "<span style=\"background-color:yellow;\">" + titleSlice + "</span>" + str.slice(indices[i]+searchStr.length)
    }
    return str;
}

    
