      function getUrlVars() {
        var vars = {};
        var parts = window.location.href.replace(/[?&]+([^=&]+)=([^&]*)/gi, function(m,key,value) {
            vars[key] = value;
        });
        return vars;
      }

      function scrubSearchString(searchString) {
        searchString = searchString.replace(/"/g, '');
        searchString = searchString.replace(/%20/g, ' ');
        searchString = searchString.replace(/<br>/, ' ');
        searchString = searchString.replace("+","");
        searchString = searchString.replace("(","");
        searchString = searchString.replace(")","");
        searchString = searchString.replace("$","");
        if (searchString.slice(0,5).toLowerCase() == "table"){
            searchString = searchString.slice(6);
        }
        return searchString;
      }

